class Solution {
  public:
    // Function to find the pair of integers in the given array
    // with the maximum sum that is less than k
    pair<int, int> maxSum(vector<int> &arr, int k) {
        int n = arr.size(); // Size of the array

        // Sorting the array in ascending order
        sort(arr.begin(), arr.end());

        int maxsum = 0, a = -1, b = -1; // Initialize with invalid values
        int j = n - 1;

        // Loop to find the pair with maximum sum that is less than k
        for (int i = 0; i < j;) {
            // If the sum of the current pair is less than k,
            // update the maximum sum, and the values of a and b
            if (arr[i] + arr[j] < k) {
                if (arr[i] + arr[j] > maxsum) {
                    maxsum = arr[i] + arr[j];
                    a = arr[i];
                    b = arr[j];
                }
                i++;
            }
            // If the sum of the current pair is greater than or equal to k,
            // decrement j to move to the next pair
            else {
                j--;
            }
        }

        // Returning the pair with maximum sum, if found
        return {a, b};
    }
};

