class Solution {
    // Method to find the pair of integers in the array with the maximum sum that is
    // less than k
    static ArrayList<Integer> maxSum(int[] arr, int k) {
        // Sorting the array in ascending order to efficiently find the required pair
        Arrays.sort(arr);

        // Initializing variables to keep track of the maximum sum, and the elements
        // forming that sum
        int maxsum = 0, a = -1,
            b = -1;             // Start with -1 to indicate no valid pair initially
        int j = arr.length - 1; // Pointer j starting from the last element of the array
        ArrayList<Integer> res = new ArrayList<>(); // To store the result pair

        // Loop through the array to find the pair with the maximum sum less than k
        for (int i = 0; i < j;) {
            // If the sum of current elements is less than k
            if (arr[i] + arr[j] < k) {
                // Update maxsum, a, and b if the current sum is greater than the
                // previous maxsum
                if (arr[i] + arr[j] > maxsum) {
                    maxsum = arr[i] + arr[j];
                    a = arr[i];
                    b = arr[j];
                }
                // Increment i to check for a larger sum (increasing the sum)
                i++;
            }
            // If the sum of current elements is greater than or equal to k
            else {
                // Decrement j to check for a smaller sum (decreasing the sum)
                j--;
            }
        }

        // Adding the elements a and b to the result list
        res.add(a);
        res.add(b);

        // Returning the result list containing the pair with the maximum sum less than
        // k
        return res;
    }
}

