//Back-end complete function Template for C++
class Solution {
  public:
    // Function to check if a number is a palindrome in binary form
    // Returns true if it is a palindrome, false otherwise
    bool isPalindrome(unsigned int N) {
        string str = "";
        int i = 0;
        // Converting the number to binary form
        while (N > 0) {
            str += ((N % 2) + '0');
            N = N / 2;
            i++;
        }
        // Checking if the binary form is a palindrome
        for (int j = 0 ; j < i/2; j++)
            if (str[j] != str[i-j-1])
                return false;
        return true;
    } 

    // Function to check if a number is a palindrome in decimal form
    // Returns true if it is a palindrome, false otherwise
    bool isPalindromeDecimal(unsigned int N) {
        unsigned int rev_num = 0;
        unsigned int N1 = N;
        // Reversing the number
        while (N > 0) {
            rev_num = rev_num*10 + N%10;
            N = N /10;
        }
        // Checking if the reversed number is equal to the original number
        if (rev_num == N1)
            return true;
        return false;        
    }

    // Function to check if a number is both a palindrome in decimal and binary form
    // Returns "Yes" if it is a palindrome in both forms, "No" otherwise
    string isDeciBinPalin(unsigned int N) {
        if (isPalindromeDecimal(N) && isPalindrome(N))
            return "Yes";
        else
            return "No";
    }
};

class Solution
{
    // Function to check if a number is a palindrome in binary form
    // Returns true if it is a palindrome, false otherwise
    public bool IsPalindrome(uint N)
    {
        string str = "";
        // Converting the number to binary form
        while (N > 0)
        {
            str = (N % 2 == 0 ? "0" : "1") + str;
            N /= 2;
        }
        // Checking if the binary form is a palindrome
        int i = str.Length;
        for (int j = 0; j < i / 2; j++)
        {
            if (str[j] != str[i - j - 1])
            {
                return false;
            }
        }
        return true;
    }

    // Function to check if a number is a palindrome in decimal form
    // Returns true if it is a palindrome, false otherwise
    public bool IsPalindromeDecimal(uint N)
    {
        uint rev_num = 0;
        uint N1 = N;
        while (N > 0)
        {
            rev_num = rev_num * 10 + N % 10;
            N /= 10;
        }
        return rev_num == N1;
    }

    // Function to check if a number is both a palindrome in decimal and binary form
    // Returns "Yes" if it is a palindrome in both forms, "No" otherwise
    public string isDeciBinPalin(uint N)
    {
        if (IsPalindromeDecimal(N) && IsPalindrome(N))
        {
            return "Yes";
        }
        return "No";
    }
}
