// User function Template for JavaScript
class Solution {
    isPalindrome(n) {
        let original = n;
        let reversed = 0;

        // Reverse the digits of the number
        while (n > 0) {
            let lastDigit = n % 10;
            reversed = reversed * 10 + lastDigit;
            n = Math.floor(n / 10);
        }

        // Check if the original number is the same as the reversed number
        return original === reversed;
    }
}
