class Solution{
	public:
	int find(vector<int>A, vector<int>B){
		int DotProduct = A[0] * B[0] + A[1] * B[1] + A[2] * B[2];
		int CrossProduct = (A[1] * B[2] - A[2] * B[1]) * (A[1] * B[2] - A[2] * B[1]) + (A[0] * B[2]  - A[2] * B[0]) * (A[0] * B[2]  - A[2] * B[0]) + (A[0] * B[1] - A[1] * B[0]) * (A[0] * B[1] - A[1] * B[0]);
		if(CrossProduct == 0)
			return 1;
		else if(DotProduct  == 0)
			return 2;
		return 0;
	}
};

