//Back-end complete function template for C++

class Solution{
public:
	//Function to sort a portion of the array between indices l and r.
	void partSort(vector<int> &arr, int n, int l, int r)
	{
		//Swapping l and r if l is greater than r.
		if(l > r)
			swap(l, r);

		//Sorting the portion of the array between indices l and r.
		sort(arr.begin() + l, arr.begin() + r + 1);
	}
};
