// Back-end complete function Template for Java

class Solution {
    static int[] passedBy(int a, int b) {
        // increment a by 1
        a++;
        // add 2 to b
        b += 2;
        // return the updated values of a and b as an array
        return new int[]{a, b};
    }
}

