//Back-end complete function Template for C++

class Solution {
  public:
    //Function to reverse the characters in a bridge pattern.
    vector<string> revCharBridge(int N) {

        vector<string> ans;

        //iterating in reverse order for each row.
        for(int i=N; i>=1; i--)
        {
            string s;
            //if it is the first row.
            if(i == N)
            {
                //adding characters from A to A+i-1.
                for(int j='A'; j<'A'+(i-1); j++)
                    s += j;

                //adding 'A'+(i-1) in the middle.
                s += 'A'+(i-1);

                //adding characters from 'A'+(i-2) to 'A'.
                for(int j='A'+(i-2); j>='A'; j--)
                    s += j;
            }
            //if it is not the first row.
            else
            {
                //adding characters from A to A+i-1.
                for(int j='A'; j<='A'+(i-1); j++)
                    s += j;

                //adding spaces in the middle.
                for(int j=0; j<(2*N-1-2*i); j++)
                    s += ' ';

                //adding characters from 'A'+(i-1) to 'A'.
                for(int j='A'+(i-1); j>='A'; j--)
                    s += j;
            }
            //adding the string s to the vector ans.
            ans.push_back(s);
        }

        //returning the vector ans.
        return ans;
    }
};

class Solution
{
    // Function to reverse the characters in a bridge pattern.
    public List<string> revCharBridge(int N)
    {
        List<string> ans = new List<string>();

        // Iterating in reverse order for each row.
        for (int i = N; i >= 1; i--)
        {
            string s = "";

            // If it is the first row.
            if (i == N)
            {
                // Adding characters from 'A' to 'A' + (i - 2).
                for (int j = 'A'; j < 'A' + (i - 1); j++)
                    s += (char)j;

                // Adding 'A' + (i - 1) in the middle.
                s += (char)('A' + (i - 1));

                // Adding characters from 'A' + (i - 2) to 'A'.
                for (int j = 'A' + (i - 2); j >= 'A'; j--)
                    s += (char)j;
            }
            else
            {
                // Adding characters from 'A' to 'A' + i - 1.
                for (int j = 'A'; j <= 'A' + i - 1; j++)
                    s += (char)j;

                // Adding spaces in the middle.
                for (int j = 0; j < (2 * N - 1 - 2 * i); j++)
                    s += ' ';

                // Adding characters from 'A' + (i - 1) to 'A'.
                for (int j = 'A' + i - 1; j >= 'A'; j--)
                    s += (char)j;
            }

            // Adding the string s to the list.
            ans.Add(s);
        }

        // Returning the list.
        return ans;
    }
}
