class Solution {
    isPerfect(arr) {
        const n = arr.length;
        if (n === 0) return true;

        let i = 0;
        // Check for strictly increasing part
        while (i + 1 < n && arr[i] < arr[i + 1]) i++;

        // Check for constant part
        while (i + 1 < n && arr[i] === arr[i + 1]) i++;

        // Check for strictly decreasing part
        while (i + 1 < n && arr[i] > arr[i + 1]) i++;

        // If we reached the end, the array is perfect
        return i === n - 1;
    }
}
