//User function Template for javascript
/**
 * @param {number} N
 * @returns {boolean}
*/

class Solution {
    //Function to check whether the number is perfect.
    isPerfect(N)
    {
        let fact = new Array(10).fill(0);
        fact[0] = 1;
        for(let i = 1; i < 10; i++){
            fact[i] = fact[i-1]*i;
        }
        let store = N;
        let Sum = 0;
        while(N){
            Sum += fact[N%10];
            N = Math.floor(N/10);
        }
        return store === Sum ? 1 : 0;
    }
}
