class Solution {
  public:
    bool isPerfectNumber(int n) {

        // To store sum of divisors
        int sum = 1;

        // Find all divisors and add them
        for (int i = 2; i <= sqrt(n); i++) {
            if (n % i == 0) {
                if (i == n / i)
                    sum += i;
                else {
                    sum += i;
                    sum += n / i;
                }
            }
        }

        // If sum of divisors is equal to
        // n, then n is a perfect number
        if (sum == n && n != 1)
            return true;
        else
            return false;
    }
};

