class Solution:

    def isPerfectNumber(self, n: int) -> int:
        # To store sum of divisors
        sum = 1

        # Find all divisors and add them
        i = 2
        while i * i <= n:
            if n % i == 0:
                if i == n // i:
                    sum += i
                else:
                    sum += i
                    sum += n // i
            i += 1

        # If the sum of divisors is equal to n and n is not 1, then n is a perfect number
        if sum == n and n != 1:
            return 1
        else:
            return 0

