class Solution{
  public:
    int isReversible(string s, int n) { 
         int i = 0, j = s.length()-1; 
         while (i < j) { 
            if (s[i] != s[j]) 
                return 0; 
            i++; 
            j--; 
         } 
         return 1; 
    } 
};

