/**
 * @param {string} str
 * @param {number} n
 * @returns {boolean}
*/

class Solution {
    //Function to check whether a string is reversible or not.
    isReversible(str, n) {
        let i = 0;
        let j = n-1;

        //iterating over the string to check for reversal.
        while (i < j) {
            //if characters at i and j are not equal, return false.
            if (str[i] !== str[j]) {
                return false;
            }

            //incrementing i and decrementing j.
            i++;
            j--;
        }

        //if all characters match, return true.
        return true;
    }
}

