class Solution
{
    //Complete this function
    //Function to check if reverse of a given string is equal to itself or not.
    public int isReversible(string str, int n)
    {
        int i = 0, j = str.Length - 1;
        while (i < j)
        {
            if (str[i] != str[j])
                return 0;
            i++;
            j--;
        }
        return 1;
    }

}
