// Function to check if the sum of ASCII values of characters in a string is a perfect square
int isSquare(string S){
    long long sum = 0;
    // Calculating the sum of ASCII values of characters in the string
    for(int i = 0; i < S.length(); i++)
        sum += S[i];

    // Calculating the square root of the sum
    int x = sqrt(sum);

    // Checking if the square of the square root is equal to the sum
    if(x * x == sum) return 1;

    // If not, returning 0
    return 0;
}
