class Solution
{
	public:
		//Function to check if the given number is divisible by 4.
		int divisible_by_four(string s)
		{
			int n = s.size();
			//If the number has only one digit.
			if(n == 1){
				//Checking if the digit is divisible by 4.
				int num  = (s[0]-'0');
				if(num % 4 == 0)
					return 1;
				else
					return 0;
			}
			//Iterating over all possible pairs of digits in the number.
			for(int i = 0; i < n; i++){
				for(int j = i+1; j < n; j++){
					//Checking if the last two numbers are divisible by 4.
					int  num1 = (s[i]-'0')*10 + (s[j]-'0');
					int  num2 = (s[j]-'0')*10 + (s[i]-'0');

					if(num1 % 4 == 0 or num2%4 == 0)
						return 1;
				}
			}
			return 0;
		}

};
