class Solution {
  public:
  
    // Returns the area of the Triangle
    double area(int x1, int y1, int x2, int y2, int x3, int y3)
    {
       return abs((x1*(y2-y3) + x2*(y3-y1)+ x3*(y1-y2))/2.0);
    }
    
    // Function to check if the point lies inside the Triangle
    bool isInside(int x1, int y1, int x2, int y2, int x3, int y3, int x, int y)
    {   
       double A = area (x1, y1, x2, y2, x3, y3);
     
       double A1 = area (x, y, x2, y2, x3, y3);
    
       double A2 = area (x1, y1, x, y, x3, y3);
     
       double A3 = area (x1, y1, x2, y2, x, y);
       
       return (A == A1 + A2 + A3);
    }
    
    string isInsideTri(int x1, int y1, int x2, int y2, int x3, int y3, int x, int y) {
        
        if(isInside(x1,y1,x2,y2,x3,y3,x,y))
    		return "Yes";
		else
		    return "No";
    }
};

class Solution
{
    // Returns the area of the Triangle
    public double area(int x1, int y1, int x2, int y2, int x3, int y3)
    {
        return Math.Abs((x1 * (y2 - y3) + x2 * (y3 - y1) + x3 * (y1 - y2)) / 2.0);
    }

    // Function to check if the point lies inside the Triangle
    public string isInsideTri(int x1, int y1, int x2, int y2, int x3, int y3, int x, int y)
    {
        double A = area(x1, y1, x2, y2, x3, y3);
        double A1 = area(x, y, x2, y2, x3, y3);
        double A2 = area(x1, y1, x, y, x3, y3);
        double A3 = area(x1, y1, x2, y2, x, y);

        if (A == A1 + A2 + A3)
            return "Yes";
        else
            return "No";
    }
}
