//Back-end complete function template for C++

class Solution{   
public:


    // Function performing calculations
    int possibleTimings(string n)
    {

        // Array storing different numbers
        // of digits a particular digit
        // can be replaced with
        int num[10] = { 2, 7, 2, 3, 3, 4, 2, 5, 1, 2 };
    	return num[n[0] - '0'] * num[n[1] - '0'];
    }



    int numberOfPossibleTimings(string S){
        return possibleTimings(S);

    }
};

//User function Template for C#

class Solution
{
    //Complete this function
    public int numberOfPossibleTimings(string S)
    {
        // Array storing different numbers
        // of digits a particular digit
        // can be replaced with
        int[] num = { 2, 7, 2, 3, 3, 4, 2, 5, 1, 2 };
        return num[(int)Char.GetNumericValue(S[0])] * num[(int)Char.GetNumericValue(S[1])];
    }
}
