//Back-end complete function template in C++

class Solution
{
    public:
    //Function to check if given number is a power of 4 or not.
    int isPowerOfFour(unsigned int n)
    {
    int count = 0;

    //Check if there is only one bit set in n
    if ( n && !(n&(n-1)) )
    {
    	//count 0 bits before set bit
    	while(n > 1)
    	{
    	n >>= 1;
    	count += 1;
    	}	 

    	//If count is even then return true else false
    	return (count%2 == 0)? 1 :0;
    }

    // If there are more than 1 bit set, then n is not a power of 4
    return 0;
    }
};

class Solution
{
    // method to check if a given number is a power of four
    int isPowerOfFour(long n)
    {
        // if the number is 0, it is not a power of four
        if(n == 0)
            return 0;

        // loop until the number is reduced to 1
        while(n != 1)
        {
            // if the number is not divisible by 4, it is not a power of four
            if(n % 4 != 0)
                return 0;

            // reduce the number by dividing it by 4
            n = n / 4;
        }

        // if the number is reduced to 1, it is a power of four
        return 1;
    }
}

