// Back-end complete function Template for javascript

class Solution {
    // Function to check if the given number is a power of four
    isPowerOfFour(N){
        if ( N && !(N&(N-1)) ) // Checks if N is non-zero and is a power of two
        {
            let count = 0; // Variable to count the number of shifts required to reach 1
            while(N > 1)
            {
                N >>= 1; // Right shift N by 1 bit
                count += 1; // Increment the count by 1
            }    
        
            return (count%2 === 0)? 1 :0; // Return 1 if count is even, otherwise return 0
        }
    
        
        return 0; // Return 0 if N is not a power of two
    }
}

//User function Template for C#

class Solution
{
    //Complete this function
    public int isPowerOfFour(uint n)
    {
        if ( n == 0)
        {
            // Return false if n is 0
            return 0;
        }

        int count = 0;

        //Check if there is only one bit set in n
        if ( (n & (n-1)) == 0 )
        {
            //count 0 bits before set bit
            while(n > 1)
            {
                n >>= 1;
                count += 1;
            }	 

            //If count is even then return true else false
            return (count%2 == 0)? 1 : 0;
        }

        // If there are more than 1 bit set, then n is not a power of 4
        return 0;
    }
}
