// Function to calculate the sum of squares of even numbers up to given number n
// Parameters:
// n - the given number
// Returns:
// the sum of squares of even numbers up to n

class Solution
{
    public:
        long long int sum_of_square_evenNumbers(long long int n)
        {
            // Using the formula to calculate the sum of squares of even numbers
            long long int ans = (2 * n * (n + 1) * (2 * n + 1))/3;
            return ans;
        }
};

javascript.full_func:
//User function Template for javascript


/**
 * @param {number} n
 * @returns {number}
*/

class Solution{
    sum_of_square_evenNumbers(n){
        let sum = 0;
        for(let i=1; i<=n; i++){
            sum += Math.pow(2*i, 2);
        }
        return sum;
    }
}
