class Solution {
  public:
    vector<int> getFreq(int x, int n) {
        vector<int> digitFrequency(10, 0);

        // Iterate from 1 to n.
        for (int i = 1; i <= n; i++) {
            long long currentPower = 1;

            // Manually calculate x^i
            for (int j = 0; j < i; j++) {
                currentPower *= x;
            }

            // Count the frequency of each digit in the current power.
            while (currentPower > 0) {
                digitFrequency[currentPower % 10]++;
                currentPower /= 10;
            }
        }

        // Return the frequency array.
        return digitFrequency;
    }
};

class Solution {
    public int[] getFreq(int x, int n) {
        int[] digitFrequency = new int[10]; // To store the frequency of digits

        // Iterate from 1 to n
        for (int i = 1; i <= n; i++) {
            long currentPower = 1;

            // Manually calculate x^i
            for (int j = 0; j < i; j++) {
                currentPower *= x;
            }

            // Count the frequency of each digit in the current power
            while (currentPower > 0) {
                int digit = (int)(currentPower % 10);
                digitFrequency[digit]++;
                currentPower /= 10;
            }
        }

        return digitFrequency;
    }
}

class Solution:

    #Function to calculate frequency of digits in powers of x from 1 to n.
    def getFreq(self, x, n):
        freq = [0] * 10  # To store the frequency of digits

        # Calculate the power of x from 1 to n and count the frequency of digits
        for i in range(1, n + 1):
            power = x**i
            while power > 0:
                digit = power % 10
                freq[digit] += 1
                power //= 10

        return freq

class Solution {
    getFreq(x, n) {
        // Initialize digit frequency array with 10 zeros.
        const digitFrequency = new Array(10).fill(0);

        // Convert x to BigInt
        const bigX = BigInt(x);

        // Iterate from 1 to n.
        for (let i = 1; i <= n; i++) {
            let currentPower = BigInt(1);

            // Manually calculate x^i using BigInt
            for (let j = 0; j < i; j++) {
                currentPower *= bigX;
            }

            // Count the frequency of each digit in the current power.
            while (currentPower > 0) {
                digitFrequency[Number(currentPower % BigInt(10))]++;
                currentPower = currentPower / BigInt(10);
            }
        }

        // Return the frequency array.
        return digitFrequency;
    }
}
