class Solution
{
    //Complete this function
    //Function to check if a given number N is Prime or not.
    public int isPrime(int N)
    {
        if(N == 1)
            return 0;
        //Iterating from 2 till N/2 to check for factors of N.
        for(int i = 2; i <= N/2; i++){
            //If N is divisible by i, then it is not prime.
            if(N%i == 0)
                return 0;
        }
        //If no factors found, then N is prime.
        return 1;
    }
}
