class Solution {
    public static boolean[] prime=new boolean[1000007];

    // FUnction to build a Sieve of Eratosthenes
    public static void sieve(){
        Arrays.fill(prime,true);
        prime[1]=false;
        for (int p=2; p*p<=1000007-1; p++)
        {
            // If prime[p] is not changed, then it is a prime
            if (prime[p] == true)
            {
                // Update all multiples of p
                for (int i=p*2; i<=1000007-1; i += p)
                    prime[i] = false;
            }
        }
    }

    // Function to check if a number is Palindrome
    public static boolean palindrome(int a){
        int n=a;
        int r=0;
        int sum=0;
         while(n>0){    
           r=n%10;  //getting remainder  
           sum=(sum*10)+r;    
           n=n/10;    
        }
        if(sum==a){
            return true;
        }
        return false;
    }

    static Long getPPS(int a, int b) {

        sieve();

        Long sum = 0L;   // Initializing Sum as 0

        // Looping from a to b
        for(int i=a ; i<=b ; i++)
        {
            // If i is both Prime and Palindrome, we add i to Sum
            if(prime[i] && palindrome(i))
	                sum+=i;
        }

        return sum;
    }
};
