    // Function to check if a number is prime
    bool isPrime(long N)
    {
       // Loop through all numbers less than or equal to the square root of N
       // and check if N is divisible by any of them
       for (long c = 2 ; c <= sqrt(N)+1; c++ )
          if ( N%c == 0 )
              return 0; // N is not prime
       return 1; // N is prime
    }

    // Function to check if the sum of ASCII values of characters in a string is prime
    bool isPrimeString(int N, string str)
    {
    	// Get the length of the string
    	long len = str.length();

    	// Initialize the variable n to hold the sum of ASCII values
    	long int n = 0;

    	// Iterate through each character in the string and add its ASCII value to n
    	for (int i = 0; i < len; i++)
    	    n += (int)str[i];

    	// Check if n is prime using the isPrime function
    	return isPrime(n);
    }
};

