            return true;  // If N is even, it's possible to express it as the sum of 2 and N-2
        return isPrime(N - 2);  // Otherwise, check if N-2 is prime and return the result
    }
    
    static boolean isPrime(int x) { 
        // Check if x is a prime number
        for (int i=2; i*i<=x; i++) 
            if (x%i == 0) 
                return false;  // If x is divisible by any number less than or equal to its square root, it's not prime
        return true;  // If x is not divisible by any number less than or equal to its square root, it's prime
    } 
}

