class Solution:
    #Function to check if a given number can be expressed as the sum of two primes.
    def isPrime(self,x):
        i=2
        #iterating till the square root of x to check if it's divisible by any number.
        while i*i<=x:
            #if x is divisible by i, it's not prime, so return False.
            if (x%i==0):
                return False
            i+=1
        #if no divisor found, x is prime.
        return True
    
    #Function to check if a given number can be expressed as the sum of two primes.
    def isPossible(self,N):
        #if number is less than 4, it cannot be expressed as the sum of two primes.
        if N<4:
            return False
        #if number is even, it can be expressed as the sum of 2 and N-2.
        if N%2==0:
            return True
        #if number is odd, check if N-2 is prime.
        return self.isPrime(N-2)
    
    #Function to determine if a given number can be expressed as the sum of two primes.
    def isSumOfTwo (self, N):
        #if possible, return "Yes", else return "No".
        if self.isPossible(N):
            return "Yes"
        else:
            return "No"

class Solution {
    // Function to generate a list of prime numbers less than n
    generatePrimes(n) {
        let primes = [];
        let isPrime = new Array(n).fill(true);
        isPrime[0] = false;
        isPrime[1] = false;

        for (let i = 2; i * i < n; i++) {
            if (isPrime[i]) {
                for (let j = i * i; j < n; j += i) {
                    isPrime[j] = false;
                }
            }
        }

        for (let i = 2; i < n; i++) {
            if (isPrime[i]) {
                primes.push(i);
            }
        }

        return primes;
    }

    // Function to check if a given number can be expressed as the sum of two primes
    isSumOfTwo(N) {
        if (N < 4) {
            return false;
        }

        let primes = this.generatePrimes(N - 1);

        for (let i = 0; i < primes.length; i++) {
            let diff = N - primes[i];
            if (primes.includes(diff)) {
                return true;
            }
        }

        return false;
    }
}
