/**
 * @param {number[]} arr - Accepts an array of numbers as input.
 * @param {number} n - Accepts the length of the array as input.
 */
class Solution {
    printArray(arr) { // Define a function to print the elements of the array.
        let n = arr.length;
        let s = ''; // Initialize an empty string to store the concatenated elements.
        let i;      // Declare a variable to control the loop.
        for (i = 0; i < n; i++) { // Iterate through each element in the array.
            s += arr[i];          // Concatenate each element to the string.
            s += ' ';             // Add a space between each element.
        }
        console.log(s); // Print the concatenated string to the console.
    }
}
