// Solution class for displaying the elements of a linked list
class Solution {
    // Function to display the elements of a linked list
    printList(head) {
        let result = '';

        // A loop that runs till head is null
        while (head !== null) {

            // Printing data of current node
            result += head.data + ' ';

            // Moving to the next node
            head = head.next;
        }

        console.log(result);
    }
}
