class Solution{
public:
    void find_pattern(vector<int>&ans,int curr_val,int N,int pat)
    {
        if(pat==1)
        {
            ans.push_back(curr_val);
            if(curr_val>0)
                find_pattern(ans,curr_val-5,N,pat);
            else if(curr_val!=N)
                find_pattern(ans,curr_val+5,N,2);
        }
        else
        {
            ans.push_back(curr_val);
            if(curr_val!=N)
                find_pattern(ans,curr_val+5,N,pat);
        }
        return;
    }

    vector<int> pattern(int N)
    {
        vector<int>ans;
        int curr_val=N;
        find_pattern(ans,curr_val,N,1);
        return ans;
    }
};

class Solution{
    List<Integer> arr=new ArrayList<>();
    void f(int curr,int n,int type)
    {
        if(curr <= 0)
            type = 1;

        arr.add(curr);

        if(curr==n && type==1)
            return;

        if(type == 0)
            f(curr-5,n,type);
        else
            f(curr+5,n,type);
    }

    public List<Integer> pattern(int n) {
        f(n ,n , 0);
        return arr;
    }
}

