#include <stdio.h>

int main() {
    int n;
    scanf("%d", &n);
    // code here
    int count = n;

    for (int i = 0; i < n; i++) {
        if (i == 0) {
            count = n;
            while (count-- > 0)
                printf("* ");
        } else if (i == n - 1) {
            count = n;
            while (count-- > 0)
                printf("* ");
        } else {
            count = 2 * n - 3;
            printf("*");
            while (count-- > 0)
                printf(" ");
            printf("*");
        }
        printf("\n");
    }

    return 0;
}

