//Back-end complete function template in C++

class Solution {
  public:
    // Function to get the table of a given number.
    vector<int> getTable(int n) {
        int ans = 1;
        vector<int> result;
        // iterating from 1 to 10 to get the multiples of n.
        for (int i = 1; i <= 10; i++) {
            ans = n * i;
            // adding the multiple to the result vector.
            result.push_back(ans);
        }
        // returning the table of n.
        return result;
    }
};

