class Solution {
  public:
    int kthDigit(int a, int b, int k) {
        // calculate a^b
        long long int x = pow(a, b);
        int temp;
        // find the Kth digit from right
        while (k--) {
            // getting last digit
            temp = x % 10;
            // remove last digit
            x = x / 10;
        }
        return temp;
    }
};

//Back-end complete function Template for Java

class Solution {
    static long kthDigit(int a, int b, int k) {
        // calculate a^b
        long x = (long)Math.pow(a, b);
        long temp = 0;
        // find the Kth digit from right
        while (k > 0) {
            // getting last digit
            temp = x % 10;
            // remove last digit
            x = x / 10;
            k--;
        }
        return temp;
    }
}

