class Solution {
    kthDigit(a, b, k) {
        // calculate a^b
        let x = Math.pow(a, b);
        let temp = 0;
        // find the Kth digit from right
        while (k > 0) {
            // getting last digit
            temp = x % 10;
            // remove last digit
            x = Math.floor(x / 10);
            k--;
        }
        return temp;
    }
}
