class Solution {
  public:
    vector<int> printPat(int n) {
        vector<int> result;

        // Loop for each row in the pattern
        for (int k = 0; k < n; k++) {
            // Loop to print numbers in descending order starting from n
            for (int i = n; i > 0; i--) {
                // Repeat each number (n - k) times in each row
                for (int j = 0; j < n - k; j++) {
                    result.push_back(i);
                }
            }
            // Append -1 after each row as a separator
            result.push_back(-1);
        }

        return result;
    }
};

