//Back-end complete function template in C++

class Solution {
    public:
    //Function to find the complex product of two arrays.
    vector<int> complexProduct(int A[], int B[]) {

        vector<int> ans(2); //Initializing the answer vector with 2 elements.

        int real = A[0], imag = B[0]; //Storing the first elements of A and B in real and imag variables respectively.

        //Iterating from index 1 to 4 (total 5 elements).
        for(int i=1; i<5; i++)
        {
            int temp = real; //Storing the value of real in a temp variable.
            real = (real*A[i]) - (imag*B[i]); //Calculating the real part of the complex product.
            imag = (temp*B[i]) + (imag*A[i]); //Calculating the imaginary part of the complex product.
        }

        ans[0] = real; //Storing the calculated real part in the first element of the answer vector.
        ans[1] = imag; //Storing the calculated imaginary part in the second element of the answer vector.

        return ans; //Returning the answer vector.
    }
};

class Solution:
    def complexProduct(self, A, B):
        real = A[0]
        img = B[0]

        for i in range(1, 5):
            r = real
            real = real*A[i]-img*B[i]
            img = r*B[i]+img*A[i]

        return [real, img]
