// Back-end complete function Template for C++

class Solution {
  public:
    // Function to calculate the product of all factors of a number
    int factorProduct(int N) {
        long long ans = 1;
        int Mod = (int)(1e9 + 7);
        // Looping through all numbers from 1 to sqrt(N)
        for (int i = 1; i * i <= N; i++) {
            // Checking if i is a factor of N
            if (N % i == 0) {
                // Multiplying ans with i
                ans *= i;
                ans %= Mod;
                // Avoiding double count by checking if N/i is not i
                if (N / i != i) {
                    // Multiplying ans with N/i
                    ans *= N / i;
                    ans %= Mod;
                }
            }
        }
        // Returning the final product of factors
        return ans;
    }
};

