class Solution {
    // Function to calculate factor product of a number
    int factorProduct(int N) {
        // Initialize the product
        long ans = 1;

        // Set the modulo value
        int Mod = (int)(1e9 + 7);

        // Loop through possible factors of N
        for (int i = 1; i * i <= N; i++) {
            // Check if i is a factor of N
            if (N % i == 0) {
                ans *= i; // Multiply the factor to the product
                ans %= Mod; // Take modulo of the product

                // Avoid double count by checking if i is not the square root of N
                if (N / i != i) {
                    ans *= N / i; // Multiply the other factor to the product
                    ans %= Mod; // Take modulo of the product
                }
            }
        }

        // Return the calculated factor product
        return (int)(ans);
    }
}

