class Solution {
    // Function to find the maximum element from array arr1 and
    // the minimum element from array arr2 and return their product.
    public long findMultiplication(int[] arr1, int[] arr2) {
        long maxx = Long.MIN_VALUE; // initializing the maximum element as minimum
                                    // possible value
        long minn = Long.MAX_VALUE; // initializing the minimum element as maximum
                                    // possible value

        // finding the maximum element from array arr1
        for (int k : arr1) {
            if (k > maxx) {
                maxx = k;
            }
        }

        // finding the minimum element from array arr2
        for (int k : arr2) {
            if (k < minn) {
                minn = k;
            }
        }

        // returning the product of maximum and minimum elements
        return maxx * minn;
    }
}

