class Solution {
    // Function to find the maximum element from array arr1 and
    // the minimum element from array arr2 and return their product.
    findMultiplication(arr1, arr2) {
        let maxx = Number.MIN_SAFE_INTEGER; // initializing the maximum element as
                                            // minimum possible value
        let minn = Number.MAX_SAFE_INTEGER; // initializing the minimum element as
                                            // maximum possible value

        // finding the maximum element from array arr1
        for (let j = 0; j < arr1.length; j++) {
            let k = arr1[j];
            if (k > maxx) maxx = k;
        }

        // finding the minimum element from array arr2
        for (let j = 0; j < arr2.length; j++) {
            let k = arr2[j];
            if (k < minn) minn = k;
        }

        // returning the product of maximum and minimum elements
        return maxx * minn;
    }
}
