//Back-end complete function Template for C++

class Solution {
  public:
    //Function to calculate the product of two consecutive digits in a given number.
    long long prodTwoDigit(long long N) {

        long long ans = 1;

        //If N is 0, the product is 0.
        if (N==0)
            ans = 0;

        //Iterating until N becomes 0.
        while(N != 0)
        {
            //Calculating the remainder of N divided by 100.
            int rem = N%100;

            //Updating the answer by multiplying it with the remainder modulo 1000000007.
            ans = (ans*rem) % 1000000007;

            //Dividing N by 100 to move to the next two digits.
            N /= 100;
        }

        //Returning the final answer.
        return ans;
    }
};

