//Back-end complete function Template for C++
class Solution {
  public:
    // function to count number of digits  
// in the product of two numbers 
long long int countDigits(long long int a, long long int b) { 
    // if either of the number is 0, then 
    // product will be 0 
    if (a == 0 || b == 0) 
        return 1; 

    // required count of digits             
    return floor(log10(abs(a)) + log10(abs(b))) + 1;     
} 
};

class Solution{
    public static long countDigits(long a, long b) 
    { 
        // if either of the number is 0, then 
        // product will be 0 
        if (a == 0 || b == 0) 
            return 1; 

        // required count of digits             
        return (long)Math.floor(Math.log10(Math.abs(a)) +  
                            Math.log10(Math.abs(b))) + 1;     
    } 

};

//User function Template for C#

class Solution
{
    //Complete this function
    public long countDigits(long a, long b)
    {
        // if either of the number is 0, then 
        // product will be 0 
        if (a == 0 || b == 0)
            return 1;

        // calculate the product
        long product = a * b;

        // calculate the number of digits in the product
        long digits = 0;
        while (product != 0)
        {
            digits++;
            product /= 10;
        }

        return digits;
    }
}
