class Solution
{
public:
    string reciprocalString(string S)
    {
        string reciprocal;
        char ch;
        for (int i = 0; i < S.size(); i++)
        {
            // converting uppercase character
            // To reciprocal character
            if (isupper(S[i]))
            {
                ch = 'Z' - S[i] + 'A';
                reciprocal.push_back(ch);
            }

            // converting lowercase character
            // To reciprocal character
            else if (islower(S[i]))
            {
                ch = 'z' - S[i] + 'a';
                reciprocal.push_back(ch);
            }

            else
            {
                reciprocal.push_back(S[i]);
            }
        }
        return reciprocal;
    }
};
