void queueTraversal(queue<int> &q) {

    while (!q.empty()) {
        cout << q.front() << " ";
        q.pop();
    }
}

//Back-end complete function Template for Java

public static void queueTraversal(Queue<Integer> q) {

    // running the till q is not Empty
    // and printing the polled elements
    while (q.size() > 0) System.out.print(q.poll() + " ");
}
