class Solution {
  public:
    // Function to calculate the number of rectangles given the dimensions
    long long rectNum(long long N, long long M) {
        // Calculate the number of rectangles using the formula (M * N * (N + 1) * (M + 1)) / 4
        // and take modulo 1000000007 for the final answer
        long long ans = ((M * N * (N + 1) * (M + 1)) / 4) % 1000000007;

        // return the calculated number of rectangles
        return ans;
    }
};

