//Back-end complete function Template for C++
//Function to count the minimum number of characters to change.

class Solution {
  public:
       int RedOrGreen(int N,string S) {
           int Red=0,Green=0;
           //iterating through the string to count the number of red and green characters.
           for(int i=0;i<N;i++){
               if(S[i]=='R')
                Red++;
               else
                Green++;
           }
           //returning the minimum count between red and green.
           return min(Red,Green);
    }
};

//backend complete function template for Java

class Solution {
    static int RedOrGreen(int N, String S) {
        int Red = 0, Green = 0;
        for (int i = 0; i < N; i++) {
            if (S.charAt(i) == 'R')
                Red++;
            else
                Green++;
        }
        return Math.min(Red, Green);
    }
}

#Back-end complete function Template for Python 3

class Solution:
    #Function to find the minimum count of 'R' and 'G' characters
    def RedOrGreen(self,N,S):
        Red,Green=0,0
        #Traversing through the given string S
        for i in range(N):
            #if the current character is 'R', increment Red count
            if(S[i]=='R'):
                Red=Red+1
            #if the current character is 'G', increment Green count
            else:
                Green=Green+1
        #returning the minimum count of 'R' and 'G' characters
        return min(Red,Green)

