class Solution {
  public:
    int remArray(int N, int arr[], int K) {
        
        long long result=1;    // Initializing the result
        
        // calculating the product of all elements in the array
        // and taking modulo K at each step to avoid overflow
        for(int j=0 ; j<N ; j++)
            result = (result*arr[j])%K;
            
        return result%K;   // returning the final result modulo K
    }
};

//User function Template for C#

class Solution
{
    //Complete this function
    //Function to calculate the remainder when arr[i] is divided by K.
    public int remArray(int N, int[] arr, int K)
    {
        //Initializing the result
        long result = 1;

        //calculating the product of all elements in the array
        //and taking modulo K at each step to avoid overflow
        for (int j = 0; j < N; j++)
            result = (result * arr[j]) % K;

        return (int)(result % K); //returning the final result modulo K
    }
}
