//Back-end complete function template for C++

class Solution {
  public:
    // Function to remove non-numeric characters from a string.
    string removeCharacters(string str) {
        int j = 0;

        // iterating over each character in the string.
        for (int i = 0; i < str.size(); i++) {
            // checking if the current character is a digit.
            if ('0' <= str[i] and str[i] <= '9')
                str[j++] = str[i]; // storing the digit at the current index j.
        }

        // returning the substring of the modified string without non-numeric
        // characters.
        return str.substr(0, j);
    }
};

public class Solution {
    public string removeCharacters(string str) {
        var result = new System.Text.StringBuilder();

        foreach (char c in str) {
            if (char.IsDigit(c)) {
                result.Append(c);
            }
        }

        return result.ToString();
    }
}

