class Solution {
  public:
    // Function to modify the given string by removing spaces
    string modify(string& s) {
        int n = s.length();
        int i = 0, itr = 0;

        // Iterate through the string
        while (i < n) {
            // Check if current character is not a space
            if (s[i] != ' ')
                // Copy the non-space character to the new string
                s[itr++] = s[i];
            i++;
        }

        // Return only the modified part of the string without any extra characters
        return s.substr(0, itr);
    }
};

class Solution {
    // Function to modify the given string by removing spaces
    public String modify(String s) {
        int n = s.length();
        int i = 0, itr = 0;
        char[] chars = s.toCharArray();
        // Iterate through the string
        while (i < n) {
            // Check if the current character is not a space
            if (chars[i] != ' ')
                // Copy the non-space character to the new string
                chars[itr++] = chars[i];
            i++;
        }
        // Return only the modified part of the string without any extra characters
        return new String(chars, 0, itr);
    }
}

class Solution:
    # Function to modify the given string by removing spaces
    def modify(self, s):
        n = len(s)
        i, itr = 0, 0
        chars = list(s)

        # Iterate through the string
        while i < n:
            # Check if the current character is not a space
            if chars[i] != ' ':
                # Copy the non-space character to the new list
                chars[itr] = chars[i]
                itr += 1
            i += 1

        # Return only the modified part of the string without any extra characters
        return ''.join(chars[:itr])

